(()=>{"use strict";window.addEventListener("scroll",(function(){document.querySelectorAll(".project").forEach((t=>{(function(t){const e=t.getBoundingClientRect();return e.top>=0&&e.left>=0&&e.bottom<=(window.innerHeight||document.documentElement.clientHeight)&&e.right<=(window.innerWidth||document.documentElement.clientWidth)})(t)&&!t.classList.contains("animate")&&t.classList.add("animate")}))})),document.getElementById("back-to-top").addEventListener("click",(()=>{window.scrollTo({top:0,behavior:"smooth"})})),document.addEventListener("DOMContentLoaded",(function(){document.querySelector(".header-link").addEventListener("click",(function(t){t.preventDefault();const e=this.getAttribute("href").substring(1),n=document.getElementById(e);if(n){const t=n.offsetTop;window.scrollTo({top:t,behavior:"smooth"})}}))}))})();